filter.script:
var comment = event.get("comment");  //获取评论字段内容

var bad_comment_total_num = 1
event.put("bad_comment_total_num",1) //所有的评论详情都是差评

if(comment.indexOf("苍蝇") != -1 ){
	//包含苍蝇字段
	event.put("key_word_fly_num",1)
}else{
	event.put("key_word_fly_num",0)
}

if(comment.indexOf("头发") != -1 ){
	//包含头发字段
	event.put("key_word_hair_num",1)
}else{
	event.put("key_word_hair_num",0)
}

if(comment.indexOf("不新鲜") != -1 ){
	//包含不干净信息
	event.put("key_word_nfresh_num",1)
}else{
	event.put("key_word_nfresh_num",0)
}
